import React from 'react'
import Sidebar from './Sidebar'
import './Books.css';
import { useState } from 'react';

function Books() {
  const [books, setBooks] = useState([
    // { id: 1,
    //   title: '',
    //   author: '',
    //   producer: '',
    //   price: '',
    //   quantity: '',
    //   category: '',
    //   purchaseDate: '',
    //   genre: '',
    //  },
  ]);
  const [showModal, setShowModal] = useState(false);
  const [newBook, setNewBook] = useState({ title: '', author: '', });
  const [editBookId, setEditBookId] = useState(null);

  const handleAddBook = () => {
    if (newBook.title && newBook.author) {
      setBooks([...books, { id: books.length + 1, title: newBook.title, author: newBook.author, producer: newBook.producer, price: newBook.price, quantity: newBook.quantity, category: newBook.category, purchaseDate: newBook.purchaseDate, genre: newBook.genre }]);
      setNewBook({ title: '',
                   author: '',
                    producer: '',
                    price: '',
                    quantity: '',
                    category: '',
                    purchaseDate: '',
                    genre: '', 
                  });
      setShowModal(false);
    }
  };

  const handleEditBook = (id) => {
    const book = books.find((b) => b.id === id);
    setNewBook({ title: book.title, author: book.author });
    setEditBookId(id);
    setShowModal(true);
  };

  const handleSaveEdit = () => {
    setBooks(books.map((book) => (book.id === editBookId ? newBook : book)));
    setNewBook({ title: '', author: '' });
    setEditBookId(null);
    setShowModal(false);
  };

  const handleDeleteBook = (id) => {
    setBooks(books.filter((book) => book.id !== id));
  };

  return (
    <div className="Books">

      <Sidebar />
      <h1>Books List</h1>
      <button className="add-button" onClick={() => setShowModal(true)}>Add Book</button>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Producer</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Category</th>
            <th>Purchase Date</th>
            <th> Genre</th>
            <th>Actions</th>

          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>
                <button onClick={() => handleEditBook(book.id)}>Edit</button>
                <button onClick={() => handleDeleteBook(book.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editBookId ? 'Edit Book' : 'Add Book'}</h2>
            <label>
              Title:
              <input
                type="text"
                value={newBook.title}
                onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
              />
            </label>
            <label>
              Author:
              <input
                type="text"
                value={newBook.author}
                onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
              />
            </label>
            <button onClick={editBookId ? handleSaveEdit : handleAddBook}>
              {editBookId ? 'Save' : 'Add'}
            </button>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};



export default Books
