import React, { useState } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import Modal from 'react-modal';
import 'react-tabs/style/react-tabs.css';
import './App.css';

Modal.setAppElement('#root');

const Re = () => {
  const [lendBooks, setLendBooks] = useState([
    {
      member: 'John Doe',
      bookTitle: 'Book 1',
      author: 'Author 1',
      issueDate: '2023-07-01',
      returnDate: '2023-07-10',
      price: '$10',
    },
    // Add more books here
  ]);

  const [returnedBooks, setReturnedBooks] = useState([
    {
      member: 'Jane Smith',
      bookTitle: 'Book 2',
      author: 'Author 2',
      issueDate: '2023-06-01',
      elapse: '30 days',
      returnDate: '2023-07-01',
      fine: '$5',
    },
    // Add more books here
  ]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newBook, setNewBook] = useState({
    member: '',
    bookTitle: '',
    author: '',
    issueDate: '',
    returnDate: '',
    price: '',
  });

  const handleReturn = (index) => {
    const bookToReturn = lendBooks[index];
    const elapse = calculateElapse(bookToReturn.issueDate, bookToReturn.returnDate);
    const fine = calculateFine(elapse);

    const updatedLendBooks = lendBooks.filter((_, i) => i !== index);
    setLendBooks(updatedLendBooks);

    setReturnedBooks([
      ...returnedBooks,
      { ...bookToReturn, elapse, fine }
    ]);
  };

  const handleEdit = (index) => {
    const bookToEdit = lendBooks[index];
    const updatedBookTitle = prompt('Edit Book Title:', bookToEdit.bookTitle);
    const updatedLendBooks = lendBooks.map((book, i) =>
      i === index ? { ...book, bookTitle: updatedBookTitle } : book
    );
    setLendBooks(updatedLendBooks);
  };

  const handleDelete = (index) => {
    const updatedLendBooks = lendBooks.filter((_, i) => i !== index);
    setLendBooks(updatedLendBooks);
  };

  const calculateElapse = (issueDate, returnDate) => {
    const issue = new Date(issueDate);
    const returnD = new Date(returnDate);
    const elapse = Math.ceil((returnD - issue) / (1000 * 60 * 60 * 24));
    return `${elapse} days`;
  };

  const calculateFine = (elapse) => {
    const days = parseInt(elapse.split(' ')[0], 10);
    return days > 30 ? `$${(days - 30) * 1}` : '$0'; // Example fine calculation
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewBook({ ...newBook, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLendBooks([...lendBooks, newBook]);
    setNewBook({
      member: '',
      bookTitle: '',
      author: '',
      issueDate: '',
      returnDate: '',
      price: '',
    });
    closeModal();
  };

  return (
    <div className="App">
      <Tabs>
        <TabList>
          <Tab>Lend Book</Tab>
          <Tab>Returned Book</Tab>
        </TabList>

        <TabPanel>
          <h2>Lend Book</h2>
          <button onClick={openModal}>Add Book</button>
          <table>
            <thead>
              <tr>
                <th>Member</th>
                <th>Book Title</th>
                <th>Author</th>
                <th>Issue Date</th>
                <th>Return Date</th>
                <th>Price</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {lendBooks.map((book, index) => (
                <tr key={index}>
                  <td>{book.member}</td>
                  <td>{book.bookTitle}</td>
                  <td>{book.author}</td>
                  <td>{book.issueDate}</td>
                  <td>{book.returnDate}</td>
                  <td>{book.price}</td>
                  <td>
                    <button onClick={() => handleReturn(index)}>Return</button>
                    <button onClick={() => handleEdit(index)}>Edit</button>
                    <button onClick={() => handleDelete(index)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </TabPanel>

        <TabPanel>
          <h2>Returned Book</h2>
          <table>
            <thead>
              <tr>
                <th>Member</th>
                <th>Book Title</th>
                <th>Author</th>
                <th>Issue Date</th>
                <th>Elapsed</th>
                <th>Return Date</th>
                <th>Fine</th>
              </tr>
            </thead>
            <tbody>
              {returnedBooks.map((book, index) => (
                <tr key={index}>
                  <td>{book.member}</td>
                  <td>{book.bookTitle}</td>
                  <td>{book.author}</td>
                  <td>{book.issueDate}</td>
                  <td>{book.elapse}</td>
                  <td>{book.returnDate}</td>
                  <td>{book.fine}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </TabPanel>
      </Tabs>

      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Add Book Modal"
        className="Modal"
        overlayClassName="Overlay"
      >
        <h2>Add a New Book</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Member:
            <input
              type="text"
              name="member"
              value={newBook.member}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Book Title:
            <input
              type="text"
              name="bookTitle"
              value={newBook.bookTitle}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Author:
            <input
              type="text"
              name="author"
              value={newBook.author}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Issue Date:
            <input
              type="date"
              name="issueDate"
              value={newBook.issueDate}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Return Date:
            <input
              type="date"
              name="returnDate"
              value={newBook.returnDate}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Price:
            <input
              type="text"
              name="price"
              value={newBook.price}
              onChange={handleChange}
              required
            />
          </label>
          <button type="submit">Add Book</button>
          <button type="button" onClick={closeModal}>Cancel</button>
        </form>
      </Modal>
    </div>
  );
};

export default Reservations;
