import React from 'react';


const BookTable = ({ books, deleteBook, editBook }) => {
  const handleEdit = (book) => {
    const updatedTitle = prompt('Enter new title', book.title);
    if (updatedTitle) {
      editBook({ ...book, title: updatedTitle });
    }
  };

  return (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Title</th>
          <th>Genre</th>
          <th>Publisher</th>
          <th>Author</th>
          <th>Purchase Date</th>
          <th>Availability</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {books.map(book => (
          <tr key={book.id}>
            <td>{book.id}</td>
            <td>{book.title}</td>
            <td>{book.publication}</td>
            <td>{book.genre}</td>
            <td>{book.publisher}</td>
            <td>{book.author}</td>
            <td>{book.purchaseDate}</td>
            <td>{book.availability ? 'Yes' : 'No'}</td>
            <td>
              <button onClick={() => handleEdit(book)}>Edit</button>
              <button onClick={() => deleteBook(book.id)}>Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default BookTable;
