import React, { useState } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import './Reservations.css';
const Reservations = () => {
  const [lendBooks, setLendBooks] = useState([
    {
      // member: '',
      // bookTitle: '',
      // author: '',
      // issueDate: '',
      // returnDate: '',
      // price: '',
    },
    // Add more books here
  ]);

  const [returnedBooks, setReturnedBooks] = useState([
    {
      // member: '',
      // bookTitle: '',
      // author: '',
      // issueDate: '',
      // elapse: '',
      // returnDate: '',
      // fine: '',
    },
    // Add more books here
  ]);

  const handleReturn = (index) => {
    const bookToReturn = lendBooks[index];
    const elapse = calculateElapse(bookToReturn.issueDate, bookToReturn.returnDate);
    const fine = calculateFine(elapse);

    const updatedLendBooks = lendBooks.filter((_, i) => i !== index);
    setLendBooks(updatedLendBooks);

    setReturnedBooks([
      ...returnedBooks,
      { ...bookToReturn, elapse, fine }
    ]);
  };

  const handleEdit = (index) => {
    const bookToEdit = lendBooks[index];
    const updatedBookTitle = prompt('Edit Book Title:', bookToEdit.bookTitle);
    const updatedLendBooks = lendBooks.map((book, i) =>
      i === index ? { ...book, bookTitle: updatedBookTitle } : book
    );
    setLendBooks(updatedLendBooks);
  };

  const handleDelete = (index) => {
    const updatedLendBooks = lendBooks.filter((_, i) => i !== index);
    setLendBooks(updatedLendBooks);
  };

  const calculateElapse = (issueDate, returnDate) => {
    const issue = new Date(issueDate);
    const returnD = new Date(returnDate);
    const elapse = Math.ceil((returnD - issue) / (1000 * 60 * 60 * 24));
    return `${elapse} days`;
  };

  const calculateFine = (elapse) => {
    const days = parseInt(elapse.split(' ')[0], 10);
    return days > 30 ? `$${(days - 30) * 1}` : '$0'; // Example fine calculation
  };

  return (
    <div className="App">
      <Tabs>
        <TabList>
          <Tab>Lend Book</Tab>
          <Tab>Returned Book</Tab>
        </TabList>

        <TabPanel>
          <h2>Lend Book</h2>
          <table>
            <thead>
              <tr>
                <th>Member</th>
                <th>Book Title</th>
                <th>Author</th>
                <th>Issue Date</th>
                <th>Return Date</th>
                <th>Price</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {lendBooks.map((book, index) => (
                <tr key={index}>
                  <td>{book.member}</td>
                  <td>{book.bookTitle}</td>
                  <td>{book.author}</td>
                  <td>{book.issueDate}</td>
                  <td>{book.returnDate}</td>
                  <td>{book.price}</td>
                  <td>
                    <button onClick={() => handleReturn(index)}>Return</button>
                    <button onClick={() => handleEdit(index)}>Edit</button>
                    <button onClick={() => handleDelete(index)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </TabPanel>

        <TabPanel>
          <h2>Returned Book</h2>
          <table>
            <thead>
              <tr>
                <th>Member</th>
                <th>Book Title</th>
                <th>Author</th>
                <th>Issue Date</th>
                <th>Elapsed</th>
                <th>Return Date</th>
                <th>Fine</th>
              </tr>
            </thead>
            <tbody>
              {returnedBooks.map((book, index) => (
                <tr key={index}>
                  <td>{book.member}</td>
                  <td>{book.bookTitle}</td>
                  <td>{book.author}</td>
                  <td>{book.issueDate}</td>
                  <td>{book.elapse}</td>
                  <td>{book.returnDate}</td>
                  <td>{book.fine}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </TabPanel>
      </Tabs>
    </div>
  );
};

export default Reservations;
